<?php  function runEditor ($inputContent, $regex) { $datasContent = $inputContent; $datasContent = preg_replace('/(<([^>]+)>)/i', '', $datasContent); for($i = 0, $l = count($regex); $i < $l; $i++) { $datasContent = preg_replace_callback($regex[$i]['desc'], $regex[$i]['output'], $datasContent); } return $datasContent; } function buildName ($titlePost) { $fileName = strtolower($titlePost); $fileName = str_replace([' ','à','á','â','ã','ä','ç','é','è','ê','ë','ì','í','î','ï','ò','ó','ô','õ','ö','š','ú','ù','û','ü','ý','ÿ','ž'], ['-','a','a','a','a','a','c','e','e','e','e','i','i','i','i','o','o','o','o','o','s','u','u','u','u','y','y','z'], $fileName); $fileName = str_replace([' ', '\''], ['-','-'], $fileName); $fileName = preg_replace('/[^a-zA-Z0-9\-]/', '', $fileName); $fileName = preg_replace('/\-{2,}/', '-', $fileName); $fileName = preg_replace('/^\-/', '', $fileName); $fileName = preg_replace('/\-$/', '', $fileName); return $fileName; }